mapper.update:
UPDATE JB_EXEC_HIST
SET JB_EXEC_ED_TM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE JB_ID = #{jbId}
      AND BIZ_AREA_ID = #{bizAreaId}
      AND TRGT_ID = #{trgtId}
      AND JB_EXEC_CNT = #{jbExecCnt}

UPDATE JB_EXEC_DTL
SET JB_ITM_EXEC_ED_TM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE JB_ID = #{jbId}
      AND BIZ_AREA_ID = #{bizAreaId}
      AND TRGT_ID = #{trgtId}
      AND JB_EXEC_CNT = #{jbExecCnt}
      AND JB_EXEC_ITM_ID = #{jbExecItmId}

mapper.select:
SELECT COUNT(*)
FROM JB_EXEC_HIST
WHERE JB_ID = #{jbId}
      AND BIZ_AREA_ID = #{bizAreaId}
      AND TRGT_ID = #{trgtId}
